mapper.select:
SELECT USER_ID
  FROM USER_TBL
 WHERE USER_ID = #{userId}
   AND USER_PW = #{userPw}

	SELECT PROD_NO, CATEGORY_NO, PROD_NAME, PROD_PRICE, PROD_STOCK, PROD_CONTENT, PROD_REG_DATE, PROD_MOD_DATE, PROD_DELETED
	  FROM PRODUCT_TBL
     WHERE PROD_NO = #{prodNo}

SELECT C.CART_NO , C.USER_NO , C.PROD_NO , C.AMOUNT , C.CART_ADD_DATE , P.PROD_NAME , P.PROD_PRICE , PS.DELIVERY_PRICE , PS.DISCOUNT_RATE FROM CART_TBL C INNER JOIN PRODUCT_TBL P ON C.PROD_NO = P.PROD_NO INNER JOIN PRODUCT_SELLER_TBL PS ON P.PROD_NO = PS.PROD_NO WHERE C.USER_NO = #{userNo}
AND C.PROD_NO IN
#{prodNo}
ORDER BY C.CART_NO DESC

	SELECT *
 	  FROM CART_TBL 
  	 WHERE USER_NO = #{userNo} AND PROD_NO = #{prodNo}

mapper.update:
UPDATE CART_TBL
   SET AMOUNT = #{amount}
 WHERE USER_NO = #{userNo}
   AND PROD_NO = #{prodNo}

mapper.select:
SELECT ADDR_NO, USER_NO, ADDR_DEFAULT, ADDR_NAME, ADDR_CONTACT, ADDR_NICKNAME, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, EXTRA_ADDRESS
  FROM ADDRESS_TBL
 WHERE USER_NO = #{userNo}

mapper.insert:

		SELECT ORDER_SEQ.NEXTVAL
		  FROM DUAL

	INSERT INTO ORDER_TBL
		 (ORDER_NO
		, USER_NO
		, ORDER_DATE
		, ORDER_TOTAL_PRICE
		, PAY_METHOD
		, ORDER_STATUS
		, SHIPPED_DATE
