		, IS_MEMBER
		, ORDER_NAME
		, CONTACT
		, POSTCODE
		, ROAD_ADDRESS
		, JIBUN_ADDRESS
		, DETAIL_ADDRESS
		, EXTRA_ADDRESS
		, ORDER_REQUEST
		, ORDER_CANCELED)
  VALUES
		 (#{orderNo}
		, #{userNo}
		, SYSDATE
		, #{orderTotalPrice}
		, #{payMethod}
		, #{orderStatus}
		, SYSDATE
		, #{membershipPoint}
		, 10
		, 0
		, #{orderName}
		, #{contact}
		, #{postcode}
		, #{roadAddress}
		, #{jibunAddress}
		, #{detailAddress}
		, #{extraAddress}
		, #{orderRequest}
		, 0
		)

INSERT INTO POINT_DETAIL_TBL
VALUES (
	   POINT_DETAIL_SEQ.NEXTVAL
	 , #{userNo}
	 , #{orderNo}
	 , 1
	 , #{pointDetail}
	 , #{pointAmount}
	 , SYSDATE
	 , ADD_MONTHS(SYSDATE, 1)
)

mapper.delete:
DELETE 
  FROM CART_TBL
 WHERE USER_NO = #{userNo} 
   AND 
		#{prodNo}

mapper.select:
SELECT ORDER_DETAIL_NO, ORDER_NO, PROD_NO, COUPON_NO, PROD_AMOUNT
  FROM ORDER_DETAIL_TBL
 WHERE ORDER_NO = #{orderNo}
